<label for="ingrediente">Ingrediente: </label><br>
<input id="ingrediente" class="form-control" autocomplete="off" autofocus value="" type="text" name="ingrediente[]"placeholder="Ingrese un ingrediente..." minlength="3" maxlength="50" pattern="([A-Za-z]{1,}[\s]{0,1}){1,}" required><br>

<label for="cantidad">Cantidad</label><br>
<input id="cantidad" class="form-control" autocomplete="off" autofocus value="" type="text" name="cantidad[]" maxlength="100" required placeholder="Ingrese una cantidad..."><br><br>